import React from 'react'
import './index.css'
let mydate = new Date(2016,2,5,5)
mydate = mydate.getHours()
let message = ''
const cssstyle = {}
if (mydate >= 1 && mydate < 12) {
    message = "Good Morning"
    cssstyle.color = 'green'
}
else if (mydate >= 12 && mydate < 17) {
    message = "Good Afternoon"
    cssstyle.color = 'orange'

}
else {
    message = "Good Night"
    cssstyle.color = 'red'
}

function App() {
    return (
        <>
            <div>
                <h1>Hello Sir, <span style={cssstyle}>{message}</span> </h1>
            </div>
        </>
    );
}

export default App;
